import React, {useState, useEffect} from 'react'
import logo1 from '../../image/avatar/1.png';
//const kid_response = document.querySelector("#app")
//const tpl;



export function KidGrid(props) {
    const data = [
        {id:1, name:'Juan1', avatar:logo1 },
        {id:2, name:'Juan2', avatar:logo1 },
        {id:3, name:'Juan3', avatar:logo1 }

    ]
    const [kids, setKids] = useState()

    useEffect(() => {
        setKids(data)
    }, [])

    return (
        <ul>
            {   !kids ? 'cargando...' :
                kids.map( (kid,index) => {
                    return <li> {kid.name} <img src= {kid.avatar} alt="hola" /></li>
             })
            }
        </ul>       
    );
}



